use std::collections::HashSet;

fn main() {
    let x = input_string();
    for i in 4..x.len() {
        let slice = &x[i-4..i];
        let vec_slice = &mut slice
            .chars()
            .collect::<Vec<char>>();
        let mut set_slice = HashSet::new();
        vec_slice.retain(|c| set_slice.insert(*c));
        if vec_slice.len() == 4 {
            println!("{:?}", &i);
            return;
        }
    }
}

fn input_string() -> &'static str {
    return ""
}
